mapper.insert:
INSERT INTO notice_tb (
    notice_id, member_id, category, title, content, is_fixed
) VALUES (
    notice_tb_seq.nextval, #{member_id}, #{category}, #{title}, #{content}, 
    0
    #{is_fixed}
)

mapper.update:
UPDATE notice_tb
SET view_count = view_count + 1
WHERE notice_id = #{noticeId}

mapper.select:
SELECT * FROM (
    SELECT ROWNUM RN, A.* FROM (
        SELECT 
            n.notice_id, n.member_id, n.category, n.title, n.content,
            n.view_count, n.is_fixed, n.created_at, n.updated_at,
            m.name AS member_name
        FROM notice_tb n
        LEFT JOIN member_tb m ON n.member_id = m.member_id
        WHERE n.category = #{category}
        AND n.is_fixed = 0
        ORDER BY n.created_at DESC
    ) A
    WHERE ROWNUM <= #{startRow} + #{pageSize}
)
WHERE RN > #{startRow}
